    private void tabletransaksiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabletransaksiMouseClicked
        // TODO add your handling code here:
        DefaultTableModel model = (DefaultTableModel)tabletransaksi.getModel();
            int row = tabletransaksi.getSelectedRow();
            transaksi tr = new transaksi();
            
            tr = transaksi.getById(Integer.parseInt(model.getValueAt(row, 0).toString()));
            txtnomor.setText(Integer.toString(tr.getNomor()));
            txtkodetransaksi.setText(tr.getKode_transaksi());
            txtnamapembeli.setText(tr.getNama_pembeli());
            txtkodeobat.setText(tr.getKode_obat());
            txtnamaobat.setText(tr.getNama_obat());
            txtmerek.setText(tr.getMerek_obat());
            txthargajual.setText(Integer.toString(tr.getHarga_jual()));
            txtjumlahbeli.setText(Integer.toString(tr.getJumlah_beli()));
            txttanggal.setText(tr.getTanggal_transaksi());
            txttotal.setText(Integer.toString(tr.getTotal_harga()));
    }//GEN-LAST:event_tabletransaksiMouseClicked

