    private void btnsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsimpanActionPerformed
        String kode = txtkodetransaksi.getText();
        String namapembeli = txtnamapembeli.getText();
        String kodeobat = txtkodeobat.getText();
        String namaobat = txtnamaobat.getText();
        String merek = txtmerek.getText();
        String hargajual = txthargajual.getText();
        String jumlahbeli = txtjumlahbeli.getText();
        String tanggal = txttanggal.getText();
        String total = txttotal.getText();
        
        if(!(kode.isEmpty() && namapembeli.isEmpty() && kodeobat.isEmpty() && namaobat.isEmpty() && merek.isEmpty() && hargajual.isEmpty() && jumlahbeli.isEmpty() && tanggal.isEmpty() && total.isEmpty())){
            transaksi tr = new transaksi();
            tr.setNomor(Integer.parseInt(txtnomor.getText()));
            tr.setKode_transaksi(kode);
            tr.setNama_pembeli(namapembeli);
            tr.setKode_obat(kodeobat);
            tr.setNama_obat(namaobat);
            tr.setMerek_obat(merek);
            tr.setHarga_jual(Integer.parseInt(txthargajual.getText()));
            tr.setJumlah_beli(Integer.parseInt(txtjumlahbeli.getText()));
            tr.setTotal_harga(Integer.parseInt(txttotal.getText()));
            tr.setTanggal_transaksi(tanggal);
            tr.save();
            
            tampilkanData();
        }
        else{
            JOptionPane.showMessageDialog(null, "Silahkan lengkapi semua data");
        }
    }//GEN-LAST:event_btnsimpanActionPerformed

