    public void tampilkanData() {
        Object[] namaKolom = new Object[] {"nomor", "kode_transaksi", "nama_pembeli", "kode_obat", "nama_obat", "merek_obat", "harga_jual", "jumlah_beli", "total_harga", "tanggal_transaksi"};
        DefaultTableModel model = new DefaultTableModel();
        model.setColumnIdentifiers(namaKolom);
        //int nomor, String kode_transaksi, String nama_pembeli, String kode_obat, String nama_obat, String merek_obat, String harga_jual, String jumlah_beli, String total_harga, String tanggal_transaksi
        ArrayList<transaksi> listTransaksi = transaksi.getAll();
        Object[] rowData = new Object[10];
        
        for (transaksi tr : listTransaksi){
            rowData[0] = tr.getNomor();
            rowData[1] = tr.getKode_transaksi();
            rowData[2] = tr.getNama_pembeli();
            rowData[3] = tr.getKode_obat();
            rowData[4] = tr.getNama_obat();
            rowData[5] = tr.getMerek_obat();
            rowData[6] = tr.getHarga_jual();
            rowData[7] = tr.getJumlah_beli();
            rowData[8] = tr.getTotal_harga();
            rowData[9] = tr.getTanggal_transaksi();
            
            model.addRow(rowData);
        }
        
        tabletransaksi.setModel(model);
        
        String[] kolom = {"Id_obat", "kode_obat", "nama_obat", "kategori_obat", "jenis_obat", "merek_obat", "harga_beli", "harga_jual", "jumlah_obat", "tanggal_masuk", "expired"};
        ArrayList<obat> list = new obat().getAll();
        Object rowDatas[] = new Object[11];
        
        tableobat.setModel(new DefaultTableModel(new Object[][] {}, kolom));
        
        for (int i = 0; i < list.size(); i++){
            rowDatas[0] = list.get(i).getId_obat();
            rowDatas[1] = list.get(i).getKode_obat();
            rowDatas[2] = list.get(i).getNama_obat();
            rowDatas[3] = list.get(i).getKategori().getNama();
            rowDatas[4] = list.get(i).getJenis_obat();
            rowDatas[5] = list.get(i).getMerek_obat();
            rowDatas[6] = list.get(i).getHarga_beli_obat();
            rowDatas[7] = list.get(i).getHarga_jual_obat();
            rowDatas[8] = list.get(i).getJumlah_obat();
            rowDatas[9] = list.get(i).getTanggal_masuk();
            rowDatas[10] = list.get(i).getExpired();
            
            ((DefaultTableModel)tableobat.getModel()).addRow(rowDatas);
        }
    }
    
    public void cari(String keyword){
        String[] kolom = {"Id_obat", "kode_obat", "nama_obat", "kategori_obat", "jenis_obat", "merek_obat", "harga_beli", "harga_jual", "jumlah_obat", "tanggal_masuk", "expired"};
        ArrayList<obat> list = new obat().search(keyword);
        Object rowData[] = new Object[11];
        
        tableobat.setModel(new DefaultTableModel(new Object[][] {}, kolom));
        
        for (obat ob : list){
            rowData[0] = ob.getId_obat();
            rowData[1] = ob.getKode_obat();
            rowData[2] = ob.getNama_obat();
            rowData[3] = ob.getKategori().getNama();
            rowData[4] = ob.getJenis_obat();
            rowData[5] = ob.getMerek_obat();
            rowData[6] = ob.getHarga_beli_obat();
            rowData[7] = ob.getHarga_jual_obat();
            rowData[8] = ob.getJumlah_obat();
            rowData[9] = ob.getTanggal_masuk();
            rowData[10] = ob.getExpired();
            
            ((DefaultTableModel)tableobat.getModel()).addRow(rowData);
        }
    }
